import { Component } from 'react';

class Lichess extends Component {

    constructor(props){
        super(props);

        this.state = {
            userData: null,
            isLoading: false,
            status: ""
        }
    }

    componentDidMount = () => {
        this.setState({isLoading: true});

        const init = {
            method: 'get',
            headers : {
                'accepts': 'application/json',
                'content-type': 'application/json',
                'Authorization': 'Bearer eOPDBXRdWyJ6TQOC',
                'Access-Control-Allow-Origin':  "https://lichess.org/api/account"
            },
            mode: 'cors'
        }

        fetch("/api/account", init)
        .then(res => res.json())
        .then(response => {
            this.setState({userData: response});
            const isOnline = this.state.userData.online;
            console.log(isOnline);
            this.setState({status: isOnline});
            this.setState({isLoading: false});
        })
        .catch(err => {
            this.setState({userData: err})
            console.log(err);
        })
    }

    render = () => {
        const status = this.state.status;
        console.log(status);

        if (this.state.isLoading){
            return (
                <h1>Is Loading...</h1>
            )
        }

        return (
                <div className="lichess">
                    <h1>Hello World!</h1>
                    <h1>{status}</h1>                
                </div>
            )
        }
}

export default Lichess;
